            rckmeldung += "<tr><td><a href=\"#\" onclick=\"window.apf.ffneTPopInNeuemTab('" + tpop_id + "')\">";
            rckmeldung += tpop.get('tpop_nr_label') + ":<\/a></td><td><a href=\"#\" onclick=\"window.apf.ffneTPopInNeuemTab('" + tpop_id + "')\">";
